%Convert to output type
LHfulltimeseries = table2array(LHfulltimeseries);

%Clear temporary variables
clear opts

%% get average of ROI timeseries
%extract data that is within ROI
ROIdata=zeros(length(ROI),693);
for ii=1:length(ROI)
    if ROI(ii,1)==1
        ROIdata(ii,:)=LHfulltimeseries(ii,:);
    else
    end
end
%remove non-ROI data (aka zeros) from the array
ROIdata = ROIdata(any(ROIdata,2),:);
%create an average timeseries for all data within the ROI
ROImean = mean( ROIdata , 1);

%% Create correlations between ROImean data and all other vertices on the cortex
LHcorr=zeros(length(LHfulltimeseries),1);
%LH
for ii=1:length(LHfulltimeseries)
    R=corrcoef(ROImean(1,:),LHfulltimeseries(ii,:));
    R=R(1,2);
    LHcorr(ii,1)=R;
end

